function net = net_struc(net,dimensions,c)    net.layers = net.layers(1:19);    n = numel(net.layers) ;    for i=1:n        %if isfield(net.layers{i}, 'weights')        if ~isempty(net.layers{i}.weights)            net.layers{i}.weights{1} = gpuArray(net.layers{i}.weights{1}) ;%            net.layers{i}.weights{2} = gpuArray(net.layers{i}.weights{2}) ;        end    end        net.layers{20}.pad = [0,0,0,0];    net.layers{20}.stride = [1,1];    net.layers{20}.type = 'conv';    net.layers{20}.name = 'fc8';               net.layers{20}.weights{1} = gpuArray(0.01*randn(1,1,4096,dimensions,'single'));%randn()   net.layers{20}.weights{2} = gpuArray(0.01*randn(1,dimensions,'single'));    net.layers{20}.weights{2} = gpuArray(0.01*randn(1,dimensions,'single'));    net.layers{20}.opts = {};    net.layers{20}.dilate = 1;    %     net.layers{21}.pad = [0,0,0,0];%     net.layers{21}.stride = [1,1];%     net.layers{21}.type = 'conv';% %     net.layers{21}.class = c;   %     net.layers{21}.name = 'fc9';%     net.layers{21}.weights{1} = gpuArray(0.01*randn(1,1,dimensions,c,'single'));%randn()ï¿½ï¿½Ì¬ï¿½Ö²ï¿½ï¿½ï¿½Î±ï¿½ï¿½ï¿½ï¿½ï¿?    net.layers{21}.weights{2} = gpuArray(0.01*randn(1,c,'single'));%     net.layers{21}.weights{2} = gpuArray(0.01*randn(1,c,'single'));%     net.layers{21}.opts = {};%     net.layers{21}.dilate = 1;%     net.layers{22}.class = c;%     net.layers{21}.type = 'softmaxloss';%     net.layers{22} = struct('type', 'softmax') ; end